<?php/** * Begins execution of the plugin. * * Since everything within the plugin is registered via hooks, * then kicking off the plugin from this point in the file does * not affect the page life cycle. * * @since    0.1.0 */function tl_admin_init() {	// Enqueue styles	wp_enqueue_style( TOOLS_LIST_NAME, plugin_dir_url( __FILE__ ) . 'css/admin.css', array(), TOOLS_LIST_VERSION, 'all' );	// wp_enqueue_style( 'wp-color-picker' );	// wp_enqueue_style('jquery-ui-css', plugin_dir_url( __FILE__ ) . 'jqueryui/1.8.2/themes/smoothness/jquery-ui.css', array(), ANCHOR_GENERATOR_VERSION, 'all' );	// wp_enqueue_style( ANCHOR_GENERATOR_NAME, plugin_dir_url( __FILE__ ) . 'css/admin.css', array(), ANCHOR_GENERATOR_VERSION, 'all' );	// Enqueue scripts	// wp_enqueue_script('jquery-ui-datepicker');	// wp_enqueue_script( ANCHOR_GENERATOR_NAME, plugin_dir_url( __FILE__ ) . 'js/admin.js', array( 'jquery', 'wp-color-picker' ), ANCHOR_GENERATOR_VERSION, false );}
function tl_load_page() {	require_once 'pages/dashboard.php';}
function tl_admin_menu() {	add_menu_page('SEO Tools List', 'SEO Tools List', 'manage_options', 'tools_list_dashboard', 'tl_load_page', 'dashicons-menu');}
function load_wp_media_files( $page ) {    // Core media script    wp_enqueue_media();
    // Your custom js file    wp_register_script('media-lib-uploader-js', plugins_url('media-lib-uploader.js' , __FILE__), array('jquery'));    wp_enqueue_script('media-lib-uploader-js');}function start_tools_list() {	add_action('admin_init', 'tl_admin_init');	add_action('admin_menu', 'tl_admin_menu');		add_action('admin_enqueue_scripts', 'load_wp_media_files');}